$(document).ready((function(){try{AOS.init({duration:1e3})}catch(e){console.warn("AOS initialization failed:",e)}})),document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelectorAll("section"),t=document.querySelectorAll(".navbar ul li a"),r=new IntersectionObserver((e=>{e.forEach((e=>{if(e.isIntersecting){t.forEach((e=>e.parentElement.classList.remove("active")));document.querySelector(`.navbar ul li a[href="#${e.target.id}"]`).parentElement.classList.add("active")}}))}),{root:null,threshold:.5});e.forEach((e=>r.observe(e))),t.forEach((e=>{e.addEventListener("click",(r=>{r.preventDefault(),t.forEach((e=>e.parentElement.classList.remove("active"))),e.parentElement.classList.add("active");document.querySelector(e.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))}))}));